            std::cerr << "curl_easy_perform() failed: " << curl_easy_strerror(res) << std::endl;
        }

        curl_easy_cleanup(curl);
    }

    curl_global_cleanup();

    if (!response.empty()) {
        rapidjson::Document document;
        document.Parse(response.c_str());

