```pawn
// Обработчик входа игрока в маркер остановки
public OnPlayerEnterCheckpoint(playerid)
{
    // Проверяем, является ли игрок автобусником
    if(IsPlayerBusDriver(playerid))
    {
        // Отправляем сообщение о прибытии автобуса
        SendClientMessage(playerid, COLOR_YELLOW, "Автобус скоро приедет!");

        // Задержка времени перед прибытием автобуса (например, 10 секунд)
        SetTimerEx("MoveBusToNextStop", 10000, false, "d", playerid);
    }
    return 1;
}

// Функция перемещения автобуса к следующей остановке
public MoveBusToNextStop(playerid)
{
    // Перемещаем автобус к следующей остановке
    SetPlayerPos(playerid, nextStopX, nextStopY, nextStopZ);

    // Задержка времени перед прибытием автобуса (например, 10 секунд)
    SetTimerEx("MoveBusToNextStop", 10000, false, "d", playerid);
}
```

