```javascript
// JavaScript code for the Snake Game

// Game constants
const GRID_SIZE = 20;
const CELL_SIZE = 20;
const INITIAL_SPEED = 200;

// Game variables
let snake = [{ x: 10, y: 10 }];
let food = { x: 15, y: 10 };
let direction = "right";
let speed = INITIAL_SPEED;
let gameLoop;

// Game board element
const gameBoard = document.getElementById("game-board");

// Initialize the game
function init() {
    createSnake();
    createFood();
    gameLoop = setInterval(moveSnake, speed);
    document.addEventListener("keydown", changeDirection);
}

// Create the snake on the game board
function createSnake() {
    snake.forEach((segment) => {
        const snakeElement = document.createElement("div");
        snakeElement.style.gridColumnStart = segment.x;
        snakeElement.style.gridRowStart = segment.y;
        snakeElement.classList.add("snake");
        gameBoard.appendChild(snakeElement);
    });
}

// Create the food on the game board
function createFood() {
    const foodElement = document.createElement("div");
    foodElement.style.gridColumnStart = food.x;
    foodElement.style.gridRowStart = food.y;
    foodElement.classList.add("food");
    gameBoard.appendChild(foodElement);
}

// Move the snake
function moveSnake() {
    const head = { x: snake[0].x, y: snake[0].y };

    switch (direction) {
        case "up":
            head.y--;
            break;
        case "down":
            head.y++;
            break;
        case "left":
            head.x--;
            break;
        case "right":
            head.x++;
            break;
    }

    snake.unshift(head);

    if (head.x === food.x && head.y === food.y) {
        eatFood();
    } else {
        snake.pop();
    }

    if (isGameOver()) {
        endGame();
    }

    updateGameBoard();
}

// Change the direction of the snake
function changeDirection(event) {
    const keyPressed = event.key;

    switch (keyPressed) {
        case "ArrowUp":
            if (direction !== "down") {
                direction = "up";
            }
            break;
        case "ArrowDown":
            if (direction !== "up") {
                direction = "down";
            }
            break;
        case "ArrowLeft":
            if (direction !== "right") {
                direction = "left";
            }
            break;
        case "ArrowRight":
            if (direction !== "left") {
                direction = "right";
            }
            break;
    }
}

// Eat the food and increase the snake's length
function eatFood() {
    const newFood = getRandomPosition();
    food = newFood;
    speed -= 10;
    clearInterval(gameLoop);
    gameLoop = setInterval(moveSnake, speed);
    createFood();
}

// Check if the game is over
function isGameOver() {
    const head = snake[0];

    if (
        head.x < 1 ||
        head.x > GRID_SIZE ||
        head.y < 1 ||
        head.y > GRID_SIZE ||
        isSnakeCollision()
    ) {
        return true;
    }

    return false;
}

// Check if the snake collides with itself
function isSnakeCollision() {
    const head = snake[0];

    for (let i = 1; i < snake.length; i++) {
        if (snake[i].x === head.x && snake[i].y === head.y) {
            return true;
        }
    }

    return false;
}

// End the game
function endGame() {
    clearInterval(gameLoop);
    gameBoard.innerHTML = '<div class="game-over">Game Over</div>';
}

// Update the game board
function updateGameBoard() {
    gameBoard.innerHTML = "";
    createSnake();
}

// Get a random position on the game board
function getRandomPosition() {
    const x = Math.floor(Math.random() * GRID_SIZE) + 1;
    const y = Math.floor(Math.random() * GRID_SIZE) + 1;
    return { x, y };
}

// Start the game
init();
