        <div class="validation_errors">
            <?= $validation->listErrors() ?>
        </div>
    <?php endif; ?>
    <?php if (isset($error)): ?>
        <div class="error_message">
            <?= $error ?>
        </div>
    <?php endif; ?>
    <form method="post" action="<?= site_url('user/login') ?>">
