import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
import org.springframework.web.bind.annotation.*;

@RestController
@RequestMapping("/api")
public class BankingController {

    @Autowired
    private UserService userService;

    @Autowired
    private TokenService tokenService;

    @Autowired
    private BCryptPasswordEncoder passwordEncoder;

    // User registration endpoint
    @PostMapping("/register")
    public ResponseEntity<String> registerUser(@RequestBody RegistrationRequest request) {
        // Validate the request
        validateRegistrationRequest(request);

        // Check if the user with the given email already exists
        if (userService.existsByEmail(request.getEmail())) {
            throw new UserAlreadyExistsException("User with email " + request.getEmail() + " already exists");
        }

        // Create a new user and save it in the database
        User user = createUserFromRegistrationRequest(request);
        userService.saveUser(user);

        // Return a success message
        return ResponseEntity.status(HttpStatus.CREATED).body("User registered successfully");
    }

    // User login endpoint
    @PostMapping("/login")
    public ResponseEntity<LoginResponse> loginUser(@RequestBody LoginRequest request) {
        // Validate the request
        validateLoginRequest(request);

        // Authenticate the user
        User user = userService.findByEmail(request.getEmail())
            .orElseThrow(() -> new UserNotFoundException("User with email " + request.getEmail() + " not found"));

        if (!passwordEncoder.matches(request.getPassword(), user.getPassword())) {
            throw new InvalidCredentialsException("Invalid email or password");
        }

        // Generate and return a JWT token
        String token = tokenService.generateToken(user.getId());
        LoginResponse response = new LoginResponse(user.getId(), token);
        return ResponseEntity.ok(response);
    }

    // Multi-factor authentication setup endpoint
    @PostMapping("/mfa/setup")
    public ResponseEntity<String> setupMFA(@RequestBody MFASetupRequest request) {
        // Validate the request
        validateMFASetupRequest(request);

        // Associate the MFA device with the user
        User user = userService.findById(request.getUserId())
            .orElseThrow(() -> new UserNotFoundException("User with ID " + request.getUserId() + " not found"));
        user.setMfaEnabled(true);
        userService.saveUser(user);

        // Return a success message
        return ResponseEntity.ok("Multi-factor authentication set up successfully");
    }

    // Fund transfer endpoint
    @PostMapping("/transfer")
    public ResponseEntity<String> transferFunds(@RequestBody TransferRequest request) {
        // Validate the request
        validateTransferRequest(request);

        // Check if the user has sufficient balance
        User user = userService.findById(request.getSenderId())
