            Debug.Log($"[{ClassName}] Waited for {_duration.TotalSeconds} seconds.");
        }
        catch (TaskCanceledException)
        {
            Debug.Log($"[{ClassName}] Task was cancelled.");
        }
    }

    private void OnDestroy()
    {
        _cancellationTokenSource.Cancel();
    }
}

