// Function to fetch wallet balances with exponential backoff
async function fetchBalances(chainId, address, privateKey) {
  let batch = [];
  let retryCount = 0;
  let delayTime = 15;
  const maxRetries = 4;

  batch.push({
    jsonrpc: '2.0',
    id: 1, 
    method: 'eth_getBalance',
    params: [address, 'latest']
  });

  batch.push({
    jsonrpc: '2.0',
    id: 2,
    method: 'particle_getTokens',
    params: [address]
  });

  while (retryCount < maxRetries) {
    try {
      let url = `https://rpc.particle.network/evm-chain?chainId=${chainId}&projectUuid=${projectUuid}&projectKey=${projectKey}`;
      let response = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(batch)
      });

      let data = await response.json();
      let errorMessage = '';
      // Process the response data
      if (data && Array.isArray(data) && data.length > 0) {
        for (let result of data) {
          if (result.error) {
            // log error if any in the batch call.
            errorMessage = errorMessage + `\nError in RPC call with ID ${result.id}: ${result.error.message}`;
            continue;  // Skip to next iteration if there was error in this call.
          }
          if (result.id === 1) { // ID matching the eth_getBalance call
            let balance = (parseInt(result.result, 16) / Math.pow(10, 18)).toFixed(18);
            if (balance > 0) {
              let balanceObj = {
                address: address,
                privateKey: privateKey,
                chainId: chainId,
                balance: balance
              };

              nonZeroBalances.push(balanceObj);
              let part1 = `${chainNames[chainId].color(chainNames[chainId].name)} ~  `,
                  part2 = `Found balance: ${balance > 0.01 ? chalk.green(balance) : chalk.redBright(balance)} `;

              console.log(part1 + part2);
            }
          }

          if(result.id === 2) {  // ID matching the particle_getTokens call
            let tokens = result.result && result.result.tokens ? result.result.tokens : [];
            for (let token of tokens) {
              let tokenBalance = (parseInt(token.amount) / Math.pow(10, token.decimals)).toFixed(token.decimals);

              if (tokenBalance > 0) {
                let tokenBalanceObj = {
                  address: address,
                  privateKey: privateKey,
                  chainId: chainId,
                  tokenName: token.name,
                  tokenBalance: tokenBalance
                };

                nonZeroBalances.push(tokenBalanceObj);
                let part3 = `${chainNames[chainId].color(chainNames[chainId].name)}: `,
                    part4 = `Found token balance: ${chalk.green(tokenBalance)} ${chalk.cyan.bold(token.name)} ~ `,
                    part5 = `on ${chainNames[chainId].name}`;

                console.log(part3 + part4 + part5);
              }
            }
          }
        }
      }
        if (errorMessage) {
          throw new Error(errorMessage);
        }

        return;
      } catch (error) {
        retryCount++;
        if (retryCount < maxRetries) {
          await delay(delayTime);
          delayTime *= 2;
        } else {
          console.log(chalk.red(`Failed to fetch balance for ${address} on chain ${chainNames[chainId].name}: ${error}`));
          failedWallets.push({
            privateKey,
            address,
            failedChains: [chainNames[chainId].name]
          });
      }
    }
  }
}
// Function to print non-zero balances
function printNonZeroBalances() {
  let output = '';
  let cleanOutput = '';
  let prevAddress = '';
  for (let balance of nonZeroBalances) {
    if (prevAddress !== balance.address) {
      prevAddress = balance.address;

      output += `~\nAddress: ${chalk.cyan(balance.address)}\n`;
      output += `Private Key: ${chalk.blue(balance.privateKey)}\n`;
      output += `Chain ID: ${chainNames[balance.chainId].color(chainNames[balance.chainId].name)}\n`;

      cleanOutput += `~\nAddress: ${balance.address}\n`;
      cleanOutput += `Private Key: ${balance.privateKey}\n`;
      cleanOutput += `Chain ID: ${chainNames[balance.chainId].name}\n`;

      if (balance.hasOwnProperty('tokenName')) {
        output += `Token: ${chalk.bold.blueBright(balance.tokenName)}\n`;
        output += `Token Balance: ${balance.tokenBalance > 0.01 ? chalk.green(balance.tokenBalance) : chalk.redBright(balance.tokenBalance)}\n~\n`;

        cleanOutput += `Token: ${balance.tokenName}\n`;
        cleanOutput += `Token Balance: ${balance.tokenBalance}\n~\n`;
      } else {
        output += `Balance: ${balance.balance > 0.01 ? chalk.green(balance.balance) : chalk.redBright(balance.balance)}\n~\n`;
        cleanOutput += `Balance: ${balance.balance}\n~\n`;
      }
    }
  }

  if (failedWallets.length > 0) {
    cleanOutput += "Failed to fetch balances for these wallets:\n";
    for (let wallet of failedWallets) {
      cleanOutput += `Priv: ${wallet.privateKey}\n`;
      cleanOutput += `-- Pub: ${wallet.address}\n`;
      cleanOutput += `~~~~ Failed chain(s): ${wallet.failedChains.join(', ')}\n`;
    }
  }

  const outputFilePath = argv.output ? argv.output : 'resultz.txt';
  fs.writeFileSync(outputFilePath, cleanOutput);
  return output;
}
