        let trimmedKeyOrAddress = keyOrAddress.trim();

        if (trimmedKeyOrAddress.startsWith('0x') && trimmedKeyOrAddress.length === 42) {
          isPublicKey = true;
        } else {
          trimmedKeyOrAddress = trimmedKeyOrAddress.replace(/^0x/, '');
        }

        if (!trimmedKeyOrAddress) continue;

