        if (isPublicKey) {
          publicAddress = trimmedKeyOrAddress;
        } else {
          const privateKeyBuffer = Buffer.from(trimmedKeyOrAddress, 'hex');
          if (privateKeyBuffer.length !== 32) {
            console.log(chalk.red(`Invalid private key: ${trimmedKeyOrAddress}`));
