          }
          const publicKeyBuffer = ethUtil.privateToPublic(privateKeyBuffer);
          publicAddress = ethUtil.bufferToHex(ethUtil.pubToAddress(publicKeyBuffer, true));
        }

        console.log(chalk.cyan(`Processing:`));
        console.log(chalk.blueBright(`Key or Address~ ${trimmedKeyOrAddress}`));
        console.log(chalk.yellow(`Searching Balance: PubKey~ ${publicAddress}`));

        const chainIds = [1, 10, 25, 56, 66, 100, 108, 128, 137, 250, 321, 324, 424, 1030, 1088, 1101, 1284, 5000, 8217, 8453, 22776, 42161, 42262, 42220, 43114, 59144, 210425, 7777777, 728126428, 1313161554, 1482601649, 1666600000 ];

        let promises = [];

        for (const chainId of chainIds) {
          promises.push(
            limiter.schedule(() => fetchBalances(chainId, publicAddress, trimmedKeyOrAddress))
          );
        }

        allPromises.push(Promise.allSettled(promises).then(() => {
          bar.tick();
        }));
      } catch (error) {
        console.log(chalk.red(`An error occurred while processing the key ${keyOrAddress}: ${error.message}`));
      }
    }

    await Promise.allSettled(allPromises);

  } catch (error) {
    console.log(chalk.red(`An error occurred: ${error.message}`));
  } finally {
    let output = printNonZeroBalances();
    console.log(output);
    console.log("Script execution completed.");
  }
})();

