function applyRainbowText() {
  const elements = document.querySelectorAll('*');
  const style = document.createElement('style');

  style.textContent = `
    @keyframes rainbow {
      from {
        color: ${getColor(1)};
      }
      to {
        color: ${getColor(2)};
      }
    }

    * {
      animation: rainbow ${getAnimationSpeed()}s infinite;
    }
  `;

