                            if 'nameserver 9.9.9.9' not in nameserver:
                                logging.debug('BT-TETHER: Added nameserver')
                                resolv.seek(0)
                                resolv.write(nameserver + 'nameserver 9.9.9.9\n')

            if any_device_connected:
