def calculate(x, y):
    if x > 10:
        if y > 10:
            return x*y
        else:
            return x+y
    else:
        return x-y

def check_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def find_max(numbers):
    max_num = numbers[0]
    for num in numbers:
        if num > max_num:
            max_num = num
    return max_num

def analyze_data(data):
    total = 0
    positive = 0
    negative = 0
    for num in data:
        total += num
        if num > 0:
            positive += 1
        elif num < 0:
            negative += 1
    average = total / len(data) if len(data) > 0 else 0
    return {"total": total, "average": average, "positive": positive, "negative": negative}

data = [10, -20, 30, -40, 50, -60]
result = analyze_data(data)
print(result)

